package AssignmentsOfJava;

public class ANDOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=0;
		int b=1;
		
		if(b>a && b==2)
			System.out.println("If condition is true");
		else
			System.out.println("Else condition");

	}

}
